import { useState } from 'react';
import { Guitars } from '../../../../types/guitar';
import {GUITAR_STEP} from '../../../../const';


type PaginationProps = {
  guitars: Guitars;
}

function Pagination({guitars}: PaginationProps): JSX.Element {
  const [currentPage, setCurrentPage] = useState(1);
  const lastGuitarIndex = currentPage * GUITAR_STEP;
  const firstGuitarIndex = lastGuitarIndex - GUITAR_STEP;
  const currentGuitar = guitars.slice(firstGuitarIndex, lastGuitarIndex);

  return (
    <div className="pagination page-content__pagination">
      <ul className="pagination__list">
        <li className="pagination__page pagination__page--prev" id="prev"><a className="link pagination__page-link" href="1">Назад</a>
        </li>
        <li className="pagination__page pagination__page--active"><a className="link pagination__page-link" href="1">1</a>
        </li>
        <li className="pagination__page"><a className="link pagination__page-link" href="2">2</a>
        </li>
        <li className="pagination__page"><a className="link pagination__page-link" href="3">3</a>
        </li>
        <li className="pagination__page pagination__page--next" id="next"><a className="link pagination__page-link" href="2">Далее</a>
        </li>
      </ul>
    </div>
  );
}

export default Pagination;
