import { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Footer from '../../components/footer/footer';
import Header from '../../components/header/header';
import Icons from '../../components/icons/icons';
import LoadingScreen from '../../components/loading-screen/loading-screen';
import RatingStars from '../../components/rating-stars/rating-stars';
import { useAppDispatch, useAppSelector } from '../../hooks';
// import { Guitar, Guitars } from '../../types/guitar';
import { guitars } from '../../mocks/guitars';
import { fetchFullGuitarAction } from '../../store/api-actions';
import { getGuitarFull, getGuitarFullLoadedDataStatus } from '../../store/guitar-full-data/selectors';
import Tabs from './components/tabs/tabs';

function GuitarFull (): JSX.Element {
  const guitarFull = useAppSelector(getGuitarFull);
  const isDataLoadedGuitarFull = useAppSelector(getGuitarFullLoadedDataStatus);

  const {id} = useParams();
  const guitarId = Number(id);

  const dispatch = useAppDispatch();

  useEffect(() => {

    if (guitarFull.id === 0 || guitarFull?.id !== guitarId) {
      dispatch(fetchFullGuitarAction(guitarId));

    }}, [guitarId, dispatch, guitarFull]);

  if (guitarFull.id === 0)  {
    if ( !isDataLoadedGuitarFull ) {
      return (
        <LoadingScreen />
      );
    }
  }

  const requiredGuitar = guitars.find((guitar) => guitar?.id === guitarId );

  if (requiredGuitar) {
    return (
      <div className="wrapper">
        <Icons/>
        <Header />
        <main className="page-content">
          <div className="container">
            <h1 className="page-content__title title title--bigger">{requiredGuitar.name}</h1>
            <ul className="breadcrumbs page-content__breadcrumbs">
              <li className="breadcrumbs__item">
                <a className="link" href="./main.html">Главная</a>
              </li>
              <li className="breadcrumbs__item">
                <a className="link" href="./main.html">Каталог</a>
              </li>
              <li className="breadcrumbs__item">
                <a className="link">{requiredGuitar.name}</a>
              </li>
            </ul>
            <div className="product-container">
              <img className="product-container__img" src={requiredGuitar.previewImg} width="90" height="235" alt={requiredGuitar.name} />
              <div className="product-container__info-wrapper">
                <h2 className="product-container__title title title--big title--uppercase">{requiredGuitar.name}</h2>
                <RatingStars
                  rating = {requiredGuitar.rating}
                />
                <Tabs guitar={requiredGuitar}/>
              </div>
              <div className="product-container__price-wrapper">
                <p className="product-container__price-info product-container__price-info--title">Цена:</p>
                <p className="product-container__price-info product-container__price-info--value">{requiredGuitar.price} ₽</p>
                <a className="button button--red button--big product-container__button" href="#">Добавить в корзину</a>
              </div>
            </div>
          </div>

        </main>

        <Footer/>
      </div>
    );
  } else {
    return (
      <>
      </>
    );
  }
}

export default GuitarFull;
