import  './add-comment-form.css';

import { FormEvent, useCallback, useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AppRoute } from '../../../../const';
import { useAppDispatch, useAppSelector } from '../../../../hooks';
import { addComment, fetchCommentsAction } from '../../../../store/api-actions';
import { reviewSendStatus } from '../../../../store/review-send-status/review-send-status';
import { getReviewSendStatus } from '../../../../store/review-send-status/selectors';
import { Guitar } from '../../../../types/guitar';

type AddCommentFormProps = {
    guitar: Guitar;
  }

function AddCommentForm({guitar}: AddCommentFormProps): JSX.Element {
  const [commentData, setCommentData] = useState<string>('');
  const [starRating, setStarRating] = useState<number>(0);
  const [nameData, setNameData] = useState<string>('');
  const [advantageData, setAdvantageData] = useState<string>('');
  const [disadvantageData, setDisadvantageData] = useState<string>('');
  const [isSending, setIsSending] = useState<boolean>(false);
  const [isDisabled, setIsDisabled] = useState<boolean>(true);
  const textarea = useRef<HTMLTextAreaElement>(null);
  const sendStatus = useAppSelector(getReviewSendStatus);
  const navigate = useNavigate();
  const dispatch = useAppDispatch();
  const guitarId = guitar.id;


  useEffect (() => () => {
    dispatch(reviewSendStatus('initial'));
  }, [dispatch]);

  const handleRatingChange = (rating: number) => {
    setStarRating(rating);
  };

  const handleCheckIsDisabled = useCallback((() => {
    setIsDisabled(starRating === 0 || nameData === '' || advantageData === '' || disadvantageData === '' || commentData === '' );
  }), [advantageData, commentData, disadvantageData, nameData, starRating]);

  useEffect(() => {
    handleCheckIsDisabled();
  }, [handleCheckIsDisabled]);

  useEffect (() => {
    if (isSending && sendStatus === 'initial') {
      dispatch(fetchCommentsAction(guitarId));
      navigate(`${AppRoute.Guitar}${guitarId}`);
    }
    setIsSending(sendStatus === 'sending');
  }, [dispatch, guitarId, isSending, navigate, sendStatus]);

  const handleSubmit = (evt: FormEvent<HTMLFormElement>) => {
    evt.preventDefault();

    if (!isDisabled) {
      const comment  = '';
      const rating = 0;
      const userName  = '';
      const advantage = '';
      const disadvantage = '';
      setCommentData(comment);
      setStarRating(rating);
      setNameData(userName);
      setAdvantageData(advantage);
      setDisadvantageData(disadvantage);
      dispatch(addComment({guitarId, comment, rating, userName, advantage, disadvantage }));
    }
  };

  return (
    <form
      action='#'
      className={`form-review ${isSending ? 'form-review__form-disabled' : ''}`}
      onSubmit={handleSubmit}
    >
      <div className="form-review__wrapper">
        <div className="form-review__name-wrapper">
          <label className="form-review__label form-review__label--required" htmlFor="user-name">Ваше Имя</label>
          <input className="form-review__input form-review__input--name" id="user-name" type="text" autoComplete="off" defaultValue={nameData} onChange={() => handleCheckIsDisabled()}/>
          {nameData !== '' ? <p className="form-review__warning">Заполните поле</p> : ''}
        </div>
        <div><span className="form-review__label form-review__label--required">Ваша Оценка</span>
          <div className="rate rate--reverse">
            <input className="visually-hidden" id="star-5" name="rate" type="radio" value="5" onChange={() => handleRatingChange}/>
            <label className="rate__label" htmlFor="star-5" title="Отлично"></label>
            <input className="visually-hidden" id="star-4" name="rate" type="radio" value="4" onChange={() => handleRatingChange}/>
            <label className="rate__label" htmlFor="star-4" title="Хорошо"></label>
            <input className="visually-hidden" id="star-3" name="rate" type="radio" value="3" onChange={() => handleRatingChange}/>
            <label className="rate__label" htmlFor="star-3" title="Нормально"></label>
            <input className="visually-hidden" id="star-2" name="rate" type="radio" value="2" onChange={() => handleRatingChange}/>
            <label className="rate__label" htmlFor="star-2" title="Плохо"></label>
            <input className="visually-hidden" id="star-1" name="rate" type="radio" value="1" onChange={() => handleRatingChange}/>
            <label className="rate__label" htmlFor="star-1" title="Ужасно"></label>
            <p className="rate__message">Поставьте оценку</p>
          </div>
        </div>
      </div>
      <label className="form-review__label form-review__label--required" htmlFor="adv">Достоинства</label>
      <input className="form-review__input" id="adv" type="text" autoComplete="off" defaultValue={advantageData} onChange={() => handleCheckIsDisabled()} />
      <p className="form-review__warning">Заполните поле</p>
      <label className="form-review__label form-review__label--required" htmlFor="disadv">Недостатки</label>
      <input className="form-review__input" id="disadv" type="text" autoComplete="off" defaultValue={disadvantageData} onChange={() => handleCheckIsDisabled()}/>
      <p className="form-review__warning">Заполните поле</p>
      <label className="form-review__label form-review__label--required" htmlFor="comment">Комментарий</label>
      <textarea
        ref={textarea}
        className="form-review__input form-review__input--textarea"
        id="comment"
        autoComplete="off"
        defaultValue={commentData}
        onChange={() => handleCheckIsDisabled()}
      >
      </textarea>
      <p className="form-review__warning">Заполните поле</p>
      <button
        className="button button--medium-20 form-review__button"
        type="submit"
        disabled={isDisabled || isSending}
      >
            Отправить отзыв
      </button>
    </form>
  );
}

export default AddCommentForm;
