import { Guitars } from '../../../../types/guitar';
import {GUITAR_STEP} from '../../../../const';


type PaginationProps = {
  guitars: Guitars;
  onPaginateChange: (number: number) => void;
  currentPage: number;
}

function Pagination({guitars, onPaginateChange, currentPage} : PaginationProps): JSX.Element {


  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(guitars.length / GUITAR_STEP); i++ ) {
    pageNumbers.push(i);
  }
  return (
    <div className="pagination page-content__pagination">
      <ul className="pagination__list">
        {currentPage !== 1
          ? <li className="pagination__page pagination__page--prev" id="prev"><a className="link pagination__page-link" href="/#">Назад</a></li>
          : ''}

        {pageNumbers.map((number) => (
          <li className="pagination__page" key={number}>
            <a className="link pagination__page-link" href='/#' onClick={()=> onPaginateChange(number)}>{number}</a>
          </li>
        ))}

        <li className="pagination__page pagination__page--next" id="next"><a className="link pagination__page-link" href="/#">Далее</a>
        </li>
      </ul>
    </div>
  );
}

export default Pagination;
