import { useCallback, useEffect, useRef, useState } from 'react';
import { useAppDispatch } from '../../../../hooks';
import { reviewSendStatus } from '../../../../store/review-send-status/review-send-status';
import { Guitar } from '../../../../types/guitar';

type AddCommentFormProps = {
    guitar: Guitar;
  }

function AddCommentForm({guitar}: AddCommentFormProps): JSX.Element {
  const textarea = useRef<HTMLTextAreaElement>(null);

  const [userNameData, setUserNameData] = useState<string>('');
  const [advantageData, setAdvantageData] = useState<string>('');
  const [disadvantageData, setDisadvantageData] = useState<string>('');
  const [commentData, setCommentData] = useState<string>('');
  const [starRating, setStarRating] = useState<number>(0);
  const [isDisabled, setIsDisabled] = useState<boolean>(true);

  const dispatch = useAppDispatch();


  useEffect (() => () => {
    dispatch(reviewSendStatus('initial'));
  }, [dispatch]);

  const handleCheckIsDisabled = useCallback((() => {
    setIsDisabled(starRating === 0);
  }), [starRating]);

  return (
    <form className="form-review">
      <div className="form-review__wrapper">
        <div className="form-review__name-wrapper">
          <label className="form-review__label form-review__label--required" htmlFor="user-name">Ваше Имя</label>
          <input
            className="form-review__input form-review__input--name"
            id="user-name"
            type="text"
            autoComplete="off"
            value={userNameData}
            onChange={(event) => setUserNameData(event.target.value)}
          />
          <p className="form-review__warning">Заполните поле</p>
        </div>
        <div><span className="form-review__label form-review__label--required">Ваша Оценка</span>
          <div className="rate rate--reverse">
            <input className="visually-hidden" id="star-5" name="rate" type="radio" value="5" onChange={(event) => setStarRating(Number(event.target.value))}/>
            <label className="rate__label" htmlFor="star-5" title="Отлично"></label>
            <input className="visually-hidden" id="star-4" name="rate" type="radio" value="4" onChange={(event) => setStarRating(Number(event.target.value))}/>
            <label className="rate__label" htmlFor="star-4" title="Хорошо"></label>
            <input className="visually-hidden" id="star-3" name="rate" type="radio" value="3" onChange={(event) => setStarRating(Number(event.target.value))}/>
            <label className="rate__label" htmlFor="star-3" title="Нормально"></label>
            <input className="visually-hidden" id="star-2" name="rate" type="radio" value="2" onChange={(event) => setStarRating(Number(event.target.value))}/>
            <label className="rate__label" htmlFor="star-2" title="Плохо"></label>
            <input className="visually-hidden" id="star-1" name="rate" type="radio" value="1" onChange={(event) => setStarRating(Number(event.target.value))}/>
            <label className="rate__label" htmlFor="star-1" title="Ужасно"></label>
            <p className="rate__message">Поставьте оценку</p>
          </div>
        </div>
      </div>
      <label className="form-review__label form-review__label--required" htmlFor="adv">Достоинства</label>
      <input
        className="form-review__input"
        id="adv"
        type="text"
        autoComplete="off"
        value={advantageData}
        onChange={(event) => setAdvantageData(event.target.value)}
      />
      <p className="form-review__warning">Заполните поле</p>
      <label className="form-review__label form-review__label--required" htmlFor="disadv">Недостатки</label>
      <input
        className="form-review__input"
        id="disadv"
        type="text"
        autoComplete="off"
        value={disadvantageData}
        onChange={(event) => setDisadvantageData(event.target.value)}
      />
      <p className="form-review__warning">Заполните поле</p>
      <label className="form-review__label form-review__label--required" htmlFor="comment">Комментарий</label>
      <textarea
        className="form-review__input form-review__input--textarea"
        id="comment"
        ref={textarea}
        autoComplete="off"
        defaultValue={commentData}
        onChange={(event) => setCommentData(event.target.value)}
      >
      </textarea>
      <p className="form-review__warning">Заполните поле</p>
      <button className="button button--medium-20 form-review__button" type="submit">Отправить отзыв</button>
    </form>

  );
}

export default AddCommentForm;
