import { Guitars } from '../../../../types/guitar';
import {FIRST_INDEX_PAGINATION, GUITAR_STEP, LAST_INDEX_PAGINATION} from '../../../../const';
import { Link } from 'react-router-dom';
import { useAppSelector } from '../../../../hooks';
import { getSelectedPagination } from '../../../../store/selected-pagination/selectors';


type PaginationProps = {
  guitars: Guitars;
  onPaginateChange: (number: number) => void;
  onPaginateBack: (number: number) => void;
  onPaginateForward: (number: number) => void;
  currentPage: number;
}

function Pagination({guitars, onPaginateChange, currentPage, onPaginateBack, onPaginateForward} : PaginationProps): JSX.Element {

  const currentPagination = useAppSelector(getSelectedPagination);

  const pageNumbers: number [] = [];
  for (let i = 1; i <= Math.ceil(guitars.length / GUITAR_STEP); i++ ) {
    pageNumbers.push(i);
  }

  const firstIndexPagination = pageNumbers[FIRST_INDEX_PAGINATION];
  const lastIndexPagination = pageNumbers[pageNumbers.length + LAST_INDEX_PAGINATION];

  return (
    <div className="pagination page-content__pagination">
      <ul className="pagination__list">
        {currentPagination !== firstIndexPagination
          ?
          <li className="pagination__page pagination__page--prev" id="prev">
            <Link className="link pagination__page-link" onClick={()=> onPaginateBack(currentPagination)} to="#">Назад</Link>
          </li>
          : ''}

        {pageNumbers.map((number) => (
          <li className={currentPagination === number ? 'pagination__page pagination__page--active' : 'pagination__page'} key={number}>
            <Link className="link pagination__page-link" to="#" onClick={()=> onPaginateChange(currentPagination)}>{number}</Link>
          </li>
        ))}

        {currentPagination !== lastIndexPagination
          ?
          <li className="pagination__page pagination__page--next" id="next">
            <Link className="link pagination__page-link" to="#" onClick={()=> onPaginateForward(currentPagination)}>Далее</Link>
          </li>
          : ''}
      </ul>
    </div>
  );
}

export default Pagination;
