import { useState } from 'react';
import { Guitar } from '../../../../types/guitar';
import { convertTypeGuitarToText } from '../../../../utils';
import classNames from 'classnames';
import { Link } from 'react-router-dom';

const getComponentByTab = (targetTab:string, guitar: Guitar) => {
  switch (targetTab) {
    case 'Характеристики':
      return (
        <div className="tabs__content" id="characteristics">
          <table className="tabs__table">
            <tbody>
              <tr className="tabs__table-row">
                <td className="tabs__title">Артикул:</td>
                <td className="tabs__value">{guitar.vendorCode}</td>
              </tr>
              <tr className="tabs__table-row">
                <td className="tabs__title">Тип:</td>
                <td className="tabs__value">{convertTypeGuitarToText(guitar.type)}</td>
              </tr>
              <tr className="tabs__table-row">
                <td className="tabs__title">Количество струн:</td>
                <td className="tabs__value">{guitar.stringCount} струнная</td>
              </tr>
            </tbody>
          </table>
        </div>
      );
    case 'Описание':
      return (
        <div className="tabs__content" id="description">
          <p className="tabs__product-description">{guitar.description}</p>
        </div>
      );
  }
};

type TabsProps = {
    guitar: Guitar;
  }

const TABS = ['Характеристики', 'Описание'];

function Tabs ({guitar}: TabsProps): JSX.Element {
  const [targetTab, setTargetTab] = useState(TABS[0]);

  return (
    <div className="tabs">
      {TABS.map((tab) => (
        <Link to={'#'}
          key={tab}
          className={classNames('button', 'button--medium', 'tabs__button', {
            'button--black-border': tab !== targetTab,
          })}
          onClick={() => setTargetTab(tab)}
        >{tab}
        </Link>
      ))}
      {getComponentByTab(targetTab, guitar)}
    </div>
  );
}

export default Tabs;
