import { convertRatingToText, getRatingAttributes } from '../../utils';

type RatingStarsProps = {
    rating: number;
    stringCount: number;
  };


const starsComponent = (rating: number) => {
  const starsAttributes = getRatingAttributes(rating);
  for (let i = 0; i < starsAttributes.length; i++) {
    if (starsAttributes[i]) {
      return (
        <svg width="12" height="11" aria-hidden="true">
          <use xlinkHref="#icon-full-star"></use>
        </svg>
      );
    } else {
      return (
        <svg width="12" height="11" aria-hidden="true">
          <use xlinkHref="#icon-star"></use>
        </svg>
      );
    }
  }
};

function RatingStars({rating, stringCount}: RatingStarsProps): JSX.Element {

  return (
    <div className="product-card__info">
      <div className="rate product-card__rate">
        {starsComponent}
        <p className="visually-hidden">Рейтинг: {convertRatingToText(rating)}</p>
        <p className="rate__count"><span className="visually-hidden">Всего оценок:</span>{stringCount}</p>
      </div>
    </div>
  );
}

export default RatingStars;
