import { createSlice } from '@reduxjs/toolkit';
import { NameSpace } from '../../const';
import { SelectedPagination } from '../../types/state';

const initialState: SelectedPagination = {
  pagePagination: 1,
};

export const selectedGenre = createSlice({
  name: NameSpace.SelectedPagination,
  initialState,
  reducers: {
    selectedPagination: (state, action) => {
      state.selectedPagination = action.payload;
    },
  },
});

export const {selectedPagination} = selectedPagination.actions;
