import { Guitars } from '../../../../types/guitar';
import {FIRST_INDEX_PAGINATION, GUITAR_STEP, LAST_INDEX_PAGINATION} from '../../../../const';


type PaginationProps = {
  guitars: Guitars;
  onPaginateChange: (number: number) => void;
  currentPage: number;
}

function Pagination({guitars, onPaginateChange, currentPage} : PaginationProps): JSX.Element {


  const pageNumbers: number [] = [];
  for (let i = 1; i <= Math.ceil(guitars.length / GUITAR_STEP); i++ ) {
    pageNumbers.push(i);
  }

  const firstIndexPagination = pageNumbers[FIRST_INDEX_PAGINATION];
  const lastIndexPagination = pageNumbers[pageNumbers.length + LAST_INDEX_PAGINATION];

  return (
    <div className="pagination page-content__pagination">
      <ul className="pagination__list">
        {currentPage !== firstIndexPagination
          ? <li className="pagination__page pagination__page--prev" id="prev"><a className="link pagination__page-link" onClick={()=> onPaginateChange(currentPage - 1)}href="/">Назад</a></li>
          : ''}

        {pageNumbers.map((number) => (
          <li className={currentPage === number ? 'pagination__page pagination__page--active' : 'pagination__page'} key={number}>
            <a className="link pagination__page-link" href='/#' onClick={()=> onPaginateChange(number)}>{number}</a>
          </li>
        ))}

        {currentPage !== lastIndexPagination
          ? <li className="pagination__page pagination__page--next" id="next"><a className="link pagination__page-link" href="/#">Далее</a></li>
          : ''}
      </ul>
    </div>
  );
}

export default Pagination;
