import { useState } from 'react';
import { GUITAR_STEP } from '../../../../const';
import { Guitars } from '../../../../types/guitar';
import CardGuitar from '../card-guitar/card-guitar';
import Pagination from '../pagination/pagination';

type GuitarsListProps = {
    guitars: Guitars;
  }

function GuitarsList({guitars}: GuitarsListProps): JSX.Element {
  const [currentPage, setCurrentPage] = useState(1);
  const lastGuitarIndex = currentPage * GUITAR_STEP;
  const firstGuitarIndex = lastGuitarIndex - GUITAR_STEP;
  const currentGuitar = guitars.slice(firstGuitarIndex, lastGuitarIndex);

  const onPaginateChange = (pageNumber: number) => setCurrentPage(pageNumber);
  const onPaginateBack = (pageNumber: number) => setCurrentPage(pageNumber - 1);
  const onPaginateForward = (pageNumber: number) => setCurrentPage(pageNumber + 1);

  return (
    <>
      <div className="cards catalog__cards">
        {currentGuitar.map((guitar) => (
          <CardGuitar
            key={guitar.id}
            guitar={guitar}
          />
        ))}
      </div>
      <Pagination
        guitars={guitars}
        onPaginateChange={onPaginateChange}
        currentPage={currentPage}
        onPaginateBack={onPaginateBack}
        onPaginateForward={onPaginateForward}
      />
    </>
  );
}

export default GuitarsList;
