import {Routes, Route} from 'react-router-dom';
import browserHistory from '../../browser-history';
import { AppRoute } from '../../const';
import { useAppSelector } from '../../hooks';
import GuitarFull from '../../pages/guitar-full/guitar-full';
import MainPage from '../../pages/main/main';
import NotFoundScreen from '../../pages/not-found-screen/not-found-screen';
import { getGuitarsLoadedDataStatus } from '../../store/guitars-data/selectors';
import { getServerStatus } from '../../store/server-status/selectors';
import HistoryRouter from '../history-route/history-route';
import LoadingScreen from '../loading-screen/loading-screen';
import ServerErorMessage from '../server-error-message/server-error-message';

function App(): JSX.Element {
  const isGuitarsLoaded = useAppSelector(getGuitarsLoadedDataStatus);
  const serverDataLoadingSattus = useAppSelector(getServerStatus);

  if (!isGuitarsLoaded) {
    return (
      <LoadingScreen />
    );
  }

  if (!serverDataLoadingSattus) {
    return (
      <ServerErorMessage />
    );
  }

  return (
    <HistoryRouter history={browserHistory}>
      <Routes>
        <Route path={AppRoute.Main}>
          <Route index element={<MainPage />} />
          <Route path='page_:idPag' element={<MainPage />} />
        </Route>

        <Route path={AppRoute.Guitar}>
          <Route index element={<GuitarFull />} />
          <Route path=':id' element={<GuitarFull />} />
        </Route>
        <Route
          path="*"
          element={<NotFoundScreen />}
        />
      </Routes>
    </HistoryRouter>
  );
}

export default App;
