import { useState } from 'react';
import { Guitar } from '../../../../types/guitar';
import { convertTypeGuitarToText } from '../../../../utils';
import classNames from 'classnames';
import { Link, Route } from 'react-router-dom';

type CharasteristicsProps = {
  currentGuitar: Guitar;
}

function Charasteristics({currentGuitar}: CharasteristicsProps): JSX.Element {
  return (
    <div className="tabs__content" id="characteristics">
      <table className="tabs__table">
        <tbody>
          <tr className="tabs__table-row">
            <td className="tabs__title">Артикул:</td>
            <td className="tabs__value">{currentGuitar.vendorCode}</td>
          </tr>
          <tr className="tabs__table-row">
            <td className="tabs__title">Тип:</td>
            <td className="tabs__value">{convertTypeGuitarToText(currentGuitar.type)}</td>
          </tr>
          <tr className="tabs__table-row">
            <td className="tabs__title">Количество струн:</td>
            <td className="tabs__value">{currentGuitar.stringCount} струнная</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

type DiscriptionProps = {
  currentGuitar: Guitar;
}

function Discription({currentGuitar}: DiscriptionProps): JSX.Element {
  return (
    <div className="tabs__content" id="description">
      <p className="tabs__product-description">{currentGuitar.description}</p>
    </div>
  );
}

type TabsProps = {
    guitar: Guitar;
  }

const TABS = ['Характеристики', 'Описание'];

function Tabs ({guitar}: TabsProps): JSX.Element {
  const [targetTab, setTargetTab] = useState(TABS[0]);

  return (
    <div className="tabs">
      {TABS.map((tab) => (
        <Link to={'#'}
          key={tab}
          className={classNames('button', 'button--medium', 'tabs__button', {
            'button--black-border': tab !== targetTab,
          })}
          onClick={() => setTargetTab(tab)}
        >{tab}
        </Link>
      ))}
      <Route path='/charasteristics' element={<Charasteristics currentGuitar={guitar}/>} />
      <Route path='/description' element={<Discription currentGuitar={guitar}/>}/>
    </div>
  );
}

export default Tabs;
